import {useCallback, useState} from "react"
import axios from 'axios'

export default function useProductsPost() {
    const [isLoading, setIsLoading] = useState(false);

    const postProducts = useCallback(async (postData) => {
      setIsLoading(true);

      try {
        const response = await axios.post('http://localhost:8000/products', postData);
        // Handle the response as needed
        console.log(response.data, 'res post');

        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }, []);

    return [postProducts, isLoading];
  }
