import {useCallback} from "react"
import axios from 'axios'

export default function useProductsPost() {
    // const [isLoading, setIsLoading] = useState(false);

    const postProducts = useCallback(async (postData) => {
      // setIsLoading(true);
      console.log(postData, 'pd')
      let config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }

      try {
        const response = await axios.post('http://localhost:8000/products', postData, config);
        // Handle the response as needed
        console.log(response.data, 'res post');

        // setIsLoading(false);
      } catch (error) {
        console.error(error.response.data);
        // setIsLoading(false);
      }
    }, []);

    return postProducts;
  }
