import React, { useState, useContext} from 'react'
// import useProductsGet from './hooks/useProductsGet';
import Table from './components/Table/Table.js';
import useProductsPost from './hooks/useProductsPost';
import { ProductDataContext } from './stores/useProductData';
import './App.css';

function App() {
  // const [{data, setData}] = useProductsGet()
  const [cost] = useState()
  const [postProducts, isLoading] = useProductsPost()
  const {productData} = useContext(ProductDataContext)

  //console.log(initialState, 'init')
  console.log(productData, 'init pd')

  //TODO: all the strings to be in a content file
  //TODO: add css modules and reset
  //TODO: add proptypes
  //TODo: disable buy now while posting data

  const handlePostData = (e) => {
    const buyData = {
      products: productData,
      total: cost,
    }
    console.log(buyData, 'buy data')
    e.preventDefault()
    postProducts(buyData)
  }

  return (
    <>
      <header>
        <h1>Review your order and complete checkout</h1>
      </header>
      <hr />
      <main>
        <div>
          <h2>Review Your Order</h2>
        </div>
        <Table isLoading={isLoading}/>
        <hr />
        <div>
          <button onClick={handlePostData} disabled={cost === 0 || isLoading}>
            {isLoading ? 'Buying...' : 'Buy now'}
          </button>
        </div>
      </main>
    </>
  );
}

export default App;
