import React, {useState, useEffect, useCallback, useContext} from 'react'
import { ProductDataContext } from '../../stores/useProductData';
import './OrderTable.css';

function OrderTable({setCost}) {
  const [hasDeleted, setHasDeleted] = useState(false)
  const [quantity, setQuantity] = useState([])
  const {productData, setProductData} = useContext(ProductDataContext)

  //Initialise with default quantities
  useEffect(() => {
    if (productData && productData.length > 0 && !hasDeleted) {
      console.log('reset?')
      setQuantity(productData.map(() => 1));
    }
  }, [productData, hasDeleted]);

  const handleIncrease = (i) => {
    setQuantity((prevQuantities) => {
      const newQuantities = [...prevQuantities];
      newQuantities[i] += 1;
      return newQuantities;
    });
    setProductData((prevData) => {
      const newData = [...prevData]
      console.log(quantity[i], 'nd')
      newData[i].quantity += 1
      return newData;
    });
  };

  const handleDecrease = (i) => {
    setQuantity((prevQuantities) => {
      const newQuantities = [...prevQuantities];
      if (newQuantities[i] > 1) {
        newQuantities[i] -= 1;
      }
      return newQuantities;
    });
  };

  //deletes quantities and item from init data
  const handleDelete = (i) => {
    setHasDeleted(true)
    setQuantity((prevQuantities) => {
      const newQuantities = [...prevQuantities];
      newQuantities.splice(i, 1);
      return newQuantities;
    });
    setProductData((prevData) => {
      const newData = [...prevData];
      newData.splice(i, 1);
      return newData;
    });
  };

  const calculateCost = (price, quantity) => {
    return (price * quantity).toFixed(2);
  };

  const calculateTotalCost = useCallback(() => {
    let total = 0;

    if (productData && productData.length > 0) {
        for (let i = 0; i < productData.length; i++) {
        total += productData[i].price * quantity[i];
        }
        setCost(total.toFixed(2));
    }

    if(quantity.length === 0) {
        setCost(0);
    }
  }, [productData, quantity, setCost]);

  useEffect(() => {
    calculateTotalCost();
  }, [calculateTotalCost, quantity]);

  return (
    <tbody>
            {productData && productData.length > 0 && productData.map((item, i) => (
                <tr key={i}>
                    <td>{item.product}</td>
                    <td>£{item.price}</td>
                    <td>
                        {quantity[i]}
                        <button
                            type="plus"
                            onClick={() => handleIncrease(i)}
                        >+</button>
                        <button
                            type="minus"
                            onClick={() => handleDecrease(i)}
                        >-</button>
                    </td>
                    <td>£{calculateCost(item.price, quantity[i])}</td>
                    <td><button
                        type="delete"
                        onClick={() => handleDelete(i)}
                    >Delete</button></td>
                </tr>
            ))}
    </tbody>     
    )
}

export default OrderTable;
