import React, {useContext, useEffect} from 'react'
import { ProductDataContext } from '../../stores/useProductData';

function TotalCosts({subTotal}) {
    const {totalCost, setTotalCost} = useContext(ProductDataContext)
    const vat = subTotal * 0.2.toFixed(2);

    useEffect(()=>{
        setTotalCost((parseFloat(subTotal) + parseFloat(vat)).toFixed(2))
    }, [subTotal, setTotalCost, vat])

  return (
    <tfoot>
        <tr>
            <td>Subtotal:</td>
            <td colSpan="3"></td>
            <td>£{subTotal}</td>
        </tr>
        <tr>
            <td>VAT (20%):</td>
            <td colSpan="3"></td>
            <td>£{vat}</td>
        </tr>
        <tr>
            <td>Total Cost:</td>
            <td colSpan="3"></td>
            <td>£{totalCost}</td>
        </tr>
    </tfoot>
    )
}

export default TotalCosts;
