import React, {useContext} from 'react'
import { ProductDataContext } from '../../stores/useProductData';

function TotalCosts() {
    const {cost} = useContext(ProductDataContext)
    const vat = (cost * 0.2).toFixed(2);

  return (
    <tfoot>
        <tr>
            <td>Subtotal:</td>
            <td colSpan="3"></td>
            <td>£{cost}</td>
        </tr>
        <tr>
            <td>VAT (20%):</td>
            <td colSpan="3"></td>
            <td>£{vat}</td>
        </tr>
        <tr>
            <td>Total Cost:</td>
            <td colSpan="3"></td>
            <td>£{(parseFloat(cost) + parseFloat(vat)).toFixed(2)}</td>
        </tr>
    </tfoot>
    )
}

export default TotalCosts;
