import React, {useState} from 'react'
import useProductsGet from './hooks/useProductsGet';
import TableHeader from './components/TableHeader/TableHeader.js';
import OrderTable from './components/OrderTable/OrderTable.js'
import TotalCosts from './components/TotalCosts/TotalCosts.js'
import useProductsPost from './hooks/useProductsPost';
import './App.css';

function App() {
  const [{data, setData, isLoading}] = useProductsGet()
  const [cost, setCost] = useState(0)
  const [postProducts, isLoadin] = useProductsPost()

  //TODO: all the strings to be in a content file
  //TODO: add css modules and reset
  //TODO: add proptypes
  //TODo: disable buy now while posting data

  const handlePostData = () => {
    const postData = { hello: 'maria' };
    postProducts(postData);
  };

  return (
    <>
      <header>
        <h1>Review your order and complete checkout</h1>
      </header>
      <hr />
      <main>
        <div>
          <h2>Review Your Order</h2>
        </div>
        {!isLoading && (
          <table>
            <TableHeader/>
            <OrderTable data={data} setData={setData} setCost={setCost}/>
            <TotalCosts cost={cost}/>
          </table>
        )}
        <hr />
        <div><button onClick={handlePostData} disabled={isLoadin || cost === 0}>{isLoading ? 'Buying...' : 'Buy now'}</button></div>
      </main>
    </>
  );
}

export default App;
